    def test_find_train_original_station(self):
        stations = find_train_original_depart_station("88")
        self.assertEqual(len(stations), 2)
        self.assertEqual(stations[0], 'S05043')
        self.assertEqual(stations[1], 'N00201')

    def test_train_status(self):
        status = get_train_status("S08640", "12286")
        self.assertIsNone(status.get('error'))

    def test_get_monitoring_trains(self):
        min_date = datetime.now() - timedelta(hours=4)
        monitor = get_trains_to_monitor(self.conn)
        for train in monitor:
            self.assertGreaterEqual(train.depart_date, min_date)

    def test_search_station(self):
        stations = search_stations("pomezia")
        self.assertIsNot(len(stations), 0)
        self.assertEqual(stations[0].name, "POMEZIA - S.PALOMBA")

        stations = search_stations("POMEZIA")
        self.assertIsNot(len(stations), 0)
        self.assertEqual(stations[0].name, "POMEZIA - S.PALOMBA")
